package com.syntax.class02;

public class ArithmethicOperactors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// +, -, *, /, %
		
		int a=900;
		int b=100;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		
		int c=10;
		int d=3;
		
		int sum=c+d;
		int sub=c-d;
		int remaider =c%d; // 10/3
		int div=c/d; // 10/3=3
		
		System.out.println("Sum is = " + sum );
		System.out.println("Sub is =" +sub);
		System.out.println("remainder is = " + remaider);
		System.out.println("Divission of integers =" +div);
		
		double num1 =10.0;
		double num2 =3.0;
				
		System.out.println("Divission of double =" +num1/num2);
		System.out.println(num1%num2);
		
		float number1= 10.0f;
		float number2=3.0F;
		
		System.out.println("Division of float=" + number1/number2);
		System.out.println(number1%number2);
		
		System.out.println("reminder examples");
		//remainder -%- mod operator
		System.out.println(16%13);
		
		
		System.out.println("what is the output"); 
		System.out.println(10+10/10); //11
		System.out.println(10);
		
		



		

		
		
		
		

	}

}
